function u(t){return new Date(t).toLocaleDateString("en-US",{year:"numeric",month:"short",day:"numeric"})}function f(t){return new Date(t).toLocaleString("en-US",{year:"numeric",month:"short",day:"numeric",hour:"2-digit",minute:"2-digit"})}function c(t){const i=new Date,a=new Date(t),r=Math.floor((i.getTime()-a.getTime())/1e3);if(r<60)return"just now";const e=Math.floor(r/60);if(e<60)return`${e} ${e===1?"minute":"minutes"} ago`;const n=Math.floor(e/60);if(n<24)return`${n} ${n===1?"hour":"hours"} ago`;const o=Math.floor(n/24);return o<7?`${o} ${o===1?"day":"days"} ago`:u(t)}function s(t){return t.charAt(0).toUpperCase()+t.slice(1)}export{s as c,f,c as g};
